/**
 *
 * @param {string} tipo
 * @param {string} item
 * @param {number} bono
 * @param {string} opcion
 * @param {boolean} porNivel
 * @param {string} categoria
 * @param {string} origen
 * @param {{}} [parametros]
 * @constructor
 * @class Bono
 */
function Bono(tipo, item, bono, opcion, porNivel,categoria,origen,parametros) {
    /** @type string */
    this.tipo = tipo;

    /** @type string */
    this.item = item;

    /** @type number */
    this.bono = bono;

    /** @type string */
    this.opcion = opcion || "";

    /** @type string */
    this.categoria = categoria || "";

    /** @type string */
    this.origen = origen ||"";

    if (this.origen == ORIGEN_CATEGORIA) {
        /** @type string */
        this.categoriaPersonaje = parametros.categoriaPersonaje;
    }

    /** @type boolean */
    this.porNivel = porNivel || false;
}

Bono.prototype = {
    constructor: Bono,

    /**
     *
     * @returns {string}
     */
    getTipo : function() {
        return this.tipo;
    },

    /**
     *
     * @returns {string}
     */
    getItem : function() {
        return this.item;
    },

    /**
     *
     * @returns {number}
     */
    getBono : function() {
        return this.bono;
    },

    /**
     *
     * @returns {string}
     */
    getBonoString : function() {
        if (this.bono < 0) {
            return "" + this.bono;
        } else {
            return "+" + this.bono;
        }
    },

    /**
     *
     * @param {number} nivel El nivel al que se quiere saber el valor del bono
     * @param {Personaje} personaje El personaje que posee el bono
     * @returns {number}
     */
    getBonoParaNivel : function(nivel, personaje) {
        var valor;
        if (this.porNivel) {
            if (this.origen == ORIGEN_CATEGORIA) {
                var nivelesDeCategoria = personaje.getNivelesDeCategoriaHastaNivel(this.categoriaPersonaje,nivel);
                valor = this.bono*nivelesDeCategoria;
            } else {
                valor = this.bono*nivel;
            }
        } else {
            valor = this.bono;
        }
        return valor;
    },

    /**
     *
     * @returns {string}
     */
    getOpcion : function() {
        return this.opcion;
    },

    /**
     *
     * @returns {string}
     */
    getCategoria : function() {
        return this.categoria;
    },

    /**
     *
     * @returns {string}
     */
    getOrigen : function() {
        return this.origen;
    },

    /**
     *
     * @returns {boolean}
     */
    isPorNivel : function() {
        return this.porNivel;
    }
};


