/**
 *
 * @class Ventaja
 * @param {string} nombre El nombre de la ventaja
 * @param {string} descripcion La descripcin de la ventaja.
 * @param {string} descripcionEfecto La descripcin del efecto mecnico de la ventaja.
 * @param {string} repetible Tipo de repetible
 * @param {number[]} coste Array de costes posibles en PC.
 * @param {ElementoProhibido[]} incompatibles Array con Ventajas incompatibles con sta.
 * @param {boolean} anulable Una vez comprada, puede darse marcha atrs y devolverla.
 * @param {string[]} elegir Array de listas de opciones a especificar
 * @param {function[]} delegadoVentaja objeto de tipo DelegadoVentaja
 * @constructor
 * @param {string} grupo
 * @param {string[]} [flagsNecesarios]
 */
function Ventaja(nombre, descripcion, descripcionEfecto, repetible, coste, incompatibles, anulable, elegir, delegadoVentaja, grupo, flagsNecesarios) {
    /**
     *
     * @type {string}
     */
    this.nombre = nombre;

    /**
     *
     * @type {string}
     */
    this.descripcion = descripcion;

    /**
     *
     * @type {string}
     */
    this.descripcionEfecto = descripcionEfecto;

    /**
     *
     * @type {string}
     */
    this.repetible = repetible;

    /**
     *
     * @type {number[]}
     */
    this.costes = coste;

    /**
     *
     * @type {ElementoProhibido[]}
     */
    this.incompatibles = incompatibles;

    /**
     *
     * @type {boolean}
     */
    this.anulable = anulable;

    /**
     *
     * @type {string[]}
     */
    this.elegir = elegir;

    /**
     *
     * @type {string[]}
     */
    this.flagsNecesarios = flagsNecesarios || [];

    /**
     *
     * @type {string}
     */
    this.grupo = grupo;

    /**
     *
     * @type {function[]}
     */
    this.delegadoVentaja = delegadoVentaja;

    /**
     *
     * @type {boolean}
     */
    this.isBasica = false;
}

Ventaja.prototype = {
    constructor : Ventaja,

    toString : function() {
        return this.nombre;
    },

    /**
     *
     * @returns {string}
     */
    getNombre : function() {
        return this.nombre;
    },

    /**
     *
     * @returns {string}
     */
    getDescripcion : function() {
        return this.descripcion;
    },

    /**
     *
     * @returns {string}
     */
    getDescripcionEfecto : function() {
        return this.descripcionEfecto;
    },

    /**
     *
     * @returns {string}
     */
    getRepetible : function() {
        return this.repetible;
    },

    /**
     *
     * @returns {number[]}
     */
    getCostes : function() {
        return this.costes;
    },

    /**
     *
     * @returns {ElementoProhibido[]}
     */
    getIncompatibles : function() {
        return this.incompatibles;
    },

    /**
     *
     * @returns {boolean}
     */
    isAnulable : function() {
        return this.anulable;
    },

    /**
     *
     * @returns {string[]}
     */
    getElegir : function() {
        return this.elegir;
    },

    /**
     *
     * @returns {string[]}
     */
    getFlagsNecesarios : function() {
        return this.flagsNecesarios;
    },

    /**
     *
     * @returns {string}
     */
    getGrupo : function() {
        return this.grupo;
    },

    /**
     *
     * @returns {Function[]}
     */
    getDelegadoVentaja : function() {
        return this.delegadoVentaja;
    },

    toJSON : function() {
        return this.nombre;
    }
};

