/**
 * Indice de tamao de las armas
 * @param tam String El tamao del arma. Puede ser TAM_ARMA_P, TAM_ARMA_M, TAM_ARMA_G, TAM_ARMA_E, TAM_ARMA_GG
 * @returns {number}
 * @throws ERR_INDICE_TAM_DESCONOCIDO
 */
function indiceTamao(tam) {
    var indice;
    switch (tam) {
        case TAM_ARMA_P:
            indice = 10;
            break;
        case TAM_ARMA_M:
            indice =  20;
            break;
        case TAM_ARMA_G:
            indice =  30;
            break;
        case TAM_ARMA_E:
            indice =  40;
            break;
        case TAM_ARMA_GG:
            indice =  50;
            break;
        default:
            throw ERR_INDICE_TAM_DESCONOCIDO + ": " + tam;
    }
    return indice;
}
