            this.numTecnicas["nivel"+this.getTecnicaKi(nombreTecnicaKi).getNivel()]--;
            this.tecnicasKi = limpiarArrayObjetosPorFuncion(this.tecnicasKi,comparaGetNombre,nombreTecnicaKi);
