                var divConjuro = getDiv(CSS_TEXTO_SMALL).addClass(CSS_VALOR_PERSONALES).addClass(CSS_TEXTO_CENTRO).addClass("four columns");
                divConjuro.append(conjuro.getNombre() + " (" +conjuro.getNivel()+")" );
