/**
 * Devuelve el nivel que corresponde a una cantidad de px, desde 1 hasta el que sea
 * @param {number} px
 * @returns {number}
 */
function getNivelPorPX(px) {
    var nivel = 0;
    var addLevel  = 0;
    if (px >= 3675) {
        var dif = px - 3675;
        addLevel = 1 + Math.floor(dif/450);
        px = 3674;
    }
    if (px < 100) {
        nivel = 1;
    } else if (px < 225) {
        nivel = 2;
    } else if (px < 375) {
        nivel = 3;
    } else if (px < 550) {
        nivel = 4;
    } else if (px < 750) {
        nivel =5;
    } else if (px < 975) {
        nivel =6;
    } else if (px < 1225) {
        nivel =7;
    } else if (px < 1500) {
        nivel =8;
    } else if (px < 1800) {
        nivel =9;
    } else if (px < 2125) {
        nivel =10;
    } else if (px < 2475) {
        nivel =11;
    } else if (px < 2850) {
        nivel =12;
    } else if (px < 3250) {
        nivel =13;
    } else if (px < 3675) {
        nivel =14;
    }

    nivel += addLevel;

    return nivel;
}

var ERR_NIVEL_ERRONEO = "Error: nivel errneo";

function getPDsPorNivel(nivel) {
    var pds = 0;
    if (nivel < 0) {
        throw ERR_NIVEL_ERRONEO;
    }
    if (nivel == 0) {
        pds = 400;
    } else {
        pds = 600 + 100 * (nivel - 1);
    }
    return pds;
}

function getAumentosCaracteristicasPorNivel(nivel) {
    var aumentos = 0;
    if (nivel < 2) {
        aumentos = 0;
    } else {
        aumentos = Math.floor(nivel/2);
    }

    return aumentos;
}

