function handleCharacterFileLoad(evt) {

    /** @type FileReader */
    var reader;

    for (var i = 0; i < evt.target.files.length; i++) {
        /**  @type FileList */
        var f = evt.target.files[i]; // FileList object
        reader = new FileReader();

        reader.onload = (function(theFile) {
            return function(e) {
                cargarPersonaje(e.target.result);
            };
        })(f);

        reader.readAsText(f);
    }
}

